import "./posts.scss";
import { useState } from "react";
import Post from "../Post/Post";
import user1 from "../../assets/unrevealed-nft-3.jpg";
import user2 from "../../assets/unrevealed-nft-2.jpg";
import defaultPic from "../../assets/default-user.png";
import AttachmentOutlinedIcon from "@mui/icons-material/AttachmentOutlined";
import CollectionsOutlinedIcon from "@mui/icons-material/CollectionsOutlined";
import EmojiEmotionsOutlinedIcon from "@mui/icons-material/EmojiEmotionsOutlined";

const Posts = () => {
  const initialPost = {
    desc: "",
  };

  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState(initialPost);

  const handleInputChange = (e) => {
    setNewPost(e.target.value);
  };

  const handleAddPost = (e) => {
    e.preventDefault();
    const configObj = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ ...newPost }),
    };
    fetch("http://localhost:3000/posts", configObj)
      .then((resp) => resp.json())
      .then((post) => setPosts(post));
  };

  const posts1 = [
    {
      id: 1,
      name: "James John",
      userId: 1,
      profilePic: user1,
      desc: "What's poppin, brand new kicks what!",
      img: "http://www.dumpaday.com/wp-content/uploads/2019/12/pictures-10-2.jpg",
    },
  ];
  return (
    <div className="posts">
      <div className="write">
        <img src={defaultPic} alt="" />
        <input
          type="text"
          placeholder="What's on your mind?"
          onChange={handleInputChange}
        />
        <div className="items">
          <AttachmentOutlinedIcon />
          <EmojiEmotionsOutlinedIcon />
          <CollectionsOutlinedIcon />
        </div>
        <button onSubmit={handleAddPost}>Post</button>
      </div>
      {posts.map((post) => (
        <Post post={post} key={post.id} />
      ))}
    </div>
  );
};

export default Posts;
