import "./posts.scss";
import { useState, useEffect } from "react";
import Post from "../Post/Post";
import user1 from "../../assets/unrevealed-nft-3.jpg";
import user2 from "../../assets/unrevealed-nft-2.jpg";
import defaultPic from "../../assets/default-user.png";
import AttachmentOutlinedIcon from "@mui/icons-material/AttachmentOutlined";
import CollectionsOutlinedIcon from "@mui/icons-material/CollectionsOutlined";
import EmojiEmotionsOutlinedIcon from "@mui/icons-material/EmojiEmotionsOutlined";

const Posts = () => {
  const initialState = {
    body: "",
  };

  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState(initialState);

  useEffect(() => {
    fetch("http://localhost:3000/posts")
      .then((resp) => resp.json())
      .then((posts) => setPosts(posts));
  }, []);

  const handleInputChange = (e) => {
    setNewPost({ body: e.target.value });
  };

  const handleAddPost = (e) => {
    e.preventDefault();
    const configObj = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPost),
    };
    fetch("http://localhost:3000/posts", configObj)
      .then((resp) => resp.json())
      .then((post) => setPosts([...posts, post]))
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="posts">
      <div className="write">
        <img src={defaultPic} alt="" />
        <input
          type="text"
          placeholder="What's on your mind?"
          onChange={handleInputChange}
          required
        />
        <div className="items">
          <AttachmentOutlinedIcon />
          <EmojiEmotionsOutlinedIcon />
          <CollectionsOutlinedIcon />
        </div>
        <button onClick={handleAddPost}>Post</button>
      </div>
      {posts.map((post) => (
        <Post post={post} key={post.id} />
      ))}
    </div>
  );
};

export default Posts;
